import PopChat from "./component/Popchat";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [messages, setMessages] = useState([
    {
      value: "How may I assist you?",
      type: "aiResponse",
    },
    // "hggvjhghgj",
    // "hello",
    // "world",
  ]);

  const [loading, setLoading] = useState(false);

  const [count, setCount] = useState(0)

  const getMessage = (value) => {
    setMessages([...messages, {value, type: 'query'}]);
    setCount(messages.length + 1)
  };

  useEffect(() => {
    console.log(messages);
    if (messages?.length > 0) {
      setLoading(true);    
    }
    if(messages.length +1 === count){
      fetch(`http://127.0.0.1:8000/answer/?query="${value}"`)
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        setMessages([...messages, {value: response.answer, type: 'aiReponse'}]);
      });
    }
  }, [messages]);

  console.log("dssd");
  return loading && <PopChat messages={messages} getMessage={getMessage} />;
}

export default App;

