// export default PopChat;import React, { useEffect, useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faRobot, faShareSquare } from '@fortawesome/free-solid-svg-icons';

const PopChat = (props) => {
  const [msg, setMsg] = useState('');
  const [chatopen, setChatopen] = useState(false);

  const messagesRef = useRef(null);

  useEffect(() => {
    if (messagesRef.current) {
      messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
    }
  }, [props.messages]);

  const toggle = () => {
    setChatopen(!chatopen);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const handleSend = () => {
    const get = props.getMessage;
    get(msg);
    setMsg('');
  };

  return (
    <div id='chatCon'>
      <div className="pop" onClick={toggle}>
        <p>
          <img alt="" />
          <FontAwesomeIcon icon={faRobot} />
        </p>
      </div>
      {chatopen && (
        <div className="chat-box">
          <div className="header" onClick={toggle}>Chat with me</div>
          <div className="msg-area" ref={messagesRef}>
            {props.messages?.length > 0 &&
              props.messages.map((msg, i) => (
                msg.type === 'query' ? (
                  <p className="right" key={i}><span>{msg.value}</span></p>
                ) : (
                  <p className="left" key={i}><span>{msg.value}</span></p>
                )
              ))}
          </div>
          <div className="footer">
            <input
              type="text"
              onChange={(e) => setMsg(e.target.value)}
              value={msg}
              onKeyDown={handleKeyPress}
            />
            <button onClick={handleSend}><FontAwesomeIcon icon={faShareSquare} /></button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PopChat;
