import React, { useState, useEffect } from 'react';
import PopChat from './components/PopChat';  // Adjust the path based on your project structure

const App = () => {
  const [messages, setMessages] = useState([]);

  // Example function to handle receiving messages
  const handleMessageReceived = (newMessage) => {
    setMessages(prevMessages => [...prevMessages, newMessage]);
  };

  // Example useEffect to simulate receiving messages over time
  useEffect(() => {
    const simulateMessageReceive = () => {
      // Simulate receiving a message after 3 seconds
      setTimeout(() => {
        handleMessageReceived('New message received!');
      }, 3000);
    };

    // Call the simulateMessageReceive function
    simulateMessageReceive();
  }, []);

  return (
    <div>
      {/* Render other components or content as needed */}
      <h1>Your React App</h1>

      {/* Render the PopChat component and pass necessary props */}
      <PopChat messages={messages} getMessage={handleMessageReceived} />
    </div>
  );
};

export default App;
