import React, { useEffect, useState } from 'react'
//import the css here

const PopChat = ( props ) => {

  const [ msg, setMsg ] = useState();
  let hide = {
    display: 'none',
  }
  let show = {
    display: 'block'
  }
  // let textRef = React.createRef()
  const {messages} = props
  console.log(messages)

  const [chatopen, setChatopen] = useState(false)
  const toggle = e => {
    setChatopen(!chatopen)
  }



  const handleKeyPress =(e)
{
  if (e.key === 'Enter') {
    // Perform the search action here
    console.log('Search for:', searchQuery);
    // You can call your search function or API request here
  }



}
const handleSend = e => {
  const get = props.getMessage
  get(msg)
  setMsg("")
}

// useEffect(() => {
//   if(messages){
//     setMsg(messages)
//   }
// },[messages])

  return (
    <div id='chatCon'>
      <div class="chat-box" style={chatopen ? show : hide}>
    <div class="header">Chat with me</div>
    <div class="msg-area">
      {
       messages?.length > 0 && messages.map((msg, i) => (
          msg.type === 'query' ? (
          <p class="right"><span>{ msg.value }</span></p>
          ) : (
            <p class="left"><span>{ msg.value }</span></p>
          )
        ))
      }

    </div>
    <div class="footer">
      <input type="text"  onChange={(e)=> setMsg(e.target.value)} value={msg}/>
      <button onClick={handleSend}>send</button>
    </div>
  </div>
    <div class="pop">
      <p><img onClick={toggle} src="https://p7.hiclipart.com/preview/151/758/442/iphone-imessage-messages-logo-computer-icons-message.jpg" alt="" /></p>
    </div>
    </div>
  )
}

export default PopChat  

































